    id int unsigned not null auto_increment primary key,
    name varchar(255) not null,
    rmb double(6,2) not null,
    imgs varchar(255) not null,
    colx varchar(32) not null,
    cxxx varchar(32) not null,
    vlxx varchar(32) not null
